  const raw0 = $json?.content?.parts?.[0]?.text ?? '';

  // bersihkan pagar ```json ... ```
  const raw = String(raw0).replace(/```json|```/gi, '').trim();

  let gender = '';
  let age = '';

  // parse JSON
  try {
    const obj = JSON.parse(raw);
    gender = String(obj?.gender ?? '').toLowerCase();
    age    = String(obj?.age_group ?? '').toLowerCase();
  } catch (e) {
    // fallback: kalau bukan JSON, pakai regex langsung dari teks
    const s = String(raw0).toLowerCase();
    if (/\b(pria|lelaki|laki(?:-|\s*)laki|laki|cowok|male|bapak|kakek)\b/i.test(s)) gender = 'pria';
    if (/\b(perempuan|wanita|cewek|female|ibu|nenek)\b/i.test(s)) gender = 'wanita';
    if (/\b(muda|remaja|teen|belia|abg|young)\b/i.test(s)) age = 'muda';
    if (!age && /\b(tua|lansia|senior|elderly|sepuh)\b/i.test(s)) age = 'tua';
  }

  // regex final untuk penentuan kategori
  const isMale   = /\b(pria|lelaki|laki(?:-|\s*)laki|laki|cowok|male|bapak|kakek)\b/i.test(gender);
  const isFemale = /\b(perempuan|wanita|cewek|female|ibu|nenek)\b/i.test(gender);
  const isYoung  = /\b(muda|remaja|teen|belia|abg|young)\b/i.test(age);

  if (isMale && isYoung)  return 'pria muda';
  if (isMale && !isYoung) return 'pria tua';
  if (isFemale && isYoung)  return 'wanita muda';
  if (isFemale && !isYoung) return 'wanita tua';

  return 'tidak terklasifikasi';
})()
}}

